-- zemoga_test_db.portfolio definition
CREATE DATABASE `zemoga_test_db` /*!40100 DEFAULT CHARACTER SET latin1 */;


CREATE TABLE `zemoga_test_db.portfolio` (
  `idportfolio` int(11) NOT NULL AUTO_INCREMENT,
  `description` varchar(255) DEFAULT NULL,
  `image_url` varchar(255) DEFAULT NULL,
  `twitter_user_name` varchar(255) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `user_id` varchar(255) NOT NULL,
  `experience_summary` varchar(255) DEFAULT NULL,
  `last_names` varchar(255) DEFAULT NULL,
  `names` varchar(255) DEFAULT NULL,
  `twitter_user_id` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`idportfolio`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

